mapper.select:
SELECT count(*)
FROM view_requirement_unitwork
WHERE udNum=#{udNum}

SELECT count(*)
FROM tbl_unitwork_definition_hist
WHERE udNum=#{udNum}

mapper.update:
UPDATE tbl_unitwork_definition_detail
SET rddNum=#{rddNum}, udId=#{udId}, udName=#{udName}, udManager=#{udManager}, extDate=#{extDate}, intDate=#{intDate}, pdDate=#{pdDate}, devDate=#{devDate}, estmate=#{estmate}
WHERE uddNum=#{uddNum}
